 1→#!/usr/bin/env node
 2→import yargs from "yargs";
 3→
 4→import { getMonitoredApiFor, NETWORK_NAMES } from "../index";
 5→
 6→// This script is expected to run against a moonbeam blockchain
 7→const argv = yargs(process.argv.slice(2))
 8→  .usage("Usage: $0")
 9→  .version("1.0.0")
10→  .options({
11→    url: {
12→      type: "string",
13→      description: "Websocket url",
14→      conflicts: ["networks"],
15→      string: true,
16→    },
17→    networks: {
18→      type: "array",
19→      choices: NETWORK_NAMES,
20→      description: "Known networks",
21→      string: true,
22→    },
23→    finalized: {
24→      type: "boolean",
25→      default: false,
26→      description: "listen to finalized only",
27→    },
28→  })
29→  .check(function (argv) {
30→    if (!argv.url && !argv.networks) {
31→      throw new Error("Error: must provide --url or --networks");
32→    }
33→    return true;
34→  }).argv;
35→
36→const main = async () => {
37→  if (argv.networks) {
38→    argv.networks.map((network) => getMonitoredApiFor({ network, finalized: argv.finalized }));
39→  } else {
40→    getMonitoredApiFor(argv);
41→  }
42→};
43→
44→main();
