83→export type NetworkOptions = {
84→  url: Options & { type: "string" };
85→  network: Options & { type: "string" };
86→  finalized: Options & { type: "boolean" };
87→};
88→
89→export type Argv = {
90→  url?: string;
91→  network?: string;
92→  finalized?: boolean;
93→};
94→
95→export const NETWORK_YARGS_OPTIONS: NetworkOptions = {
96→  url: {
97→    type: "string",
98→    description: "Websocket url",
99→    conflicts: ["network"],

110→    default: false,
111→    description: "listen to finalized only",
112→  },
113→};
114→
115→export function isKnownNetwork(name: string): name is NETWORK_NAME {
116→  return NETWORK_NAMES.includes(name as NETWORK_NAME);
117→}
118→
119→export const getWsProviderForNetwork = (name: NETWORK_NAME) => {
120→  return new WsProvider(NETWORK_WS_URLS[name]);
121→};
122→
123→// Supports providing an URL or a known network
124→export const getWsProviderFor = (argv: Argv) => {
125→  if (isKnownNetwork(argv.network)) {
126→    return getWsProviderForNetwork(argv.network);
127→  }
128→  return new WsProvider(argv.url || process.env.MOONBEAM_TOOLS_WS_URL);
129→};
130→
131→export const getHttpProviderForNetwork = (name: NETWORK_NAME) => {
132→  return new HttpProvider(NETWORK_HTTP_URLS[name]);
133→};
134→
135→// Supports providing an URL or a known network
136→export const getHttpProviderFor = (argv: Argv) => {
137→  if (isKnownNetwork(argv.network)) {
138→    return getHttpProviderForNetwork(argv.network);
139→  }
